import React from "react";
import { useNavigate } from "react-router-dom";
import CarCard from "./CarCard"; // flip card component

const Home = () => {
  const navigate = useNavigate();

  // Example car data (5 cars now)
  const carData = [
    {
      image: "/images/car1.jpg",
      title: "Mercedes S-Class",
      description: "A luxury sedan with unmatched comfort and performance."
    },
    {
      image: "/images/car2.jpg",
      title: "Range Rover Sport",
      description: "Powerful SUV for both city drives and off-road adventures."
    },
    {
      image: "/images/car3.jpg",
      title: "BMW M5",
      description: "High-performance sports sedan with a bold design."
    },
    {
      image: "/images/car4.jpg",
      title: "Audi Q7",
      description: "A premium SUV combining technology, comfort, and power."
    },
    {
      image: "/images/car5.jpg",
      title: "Porsche 911",
      description: "Iconic sports car with thrilling performance and style."
    }
  ];

  return (
    <div className="bg-cover bg-center flex flex-col items-center justify-center px-6 py-12">
      {/* Content Section */}
      <div className="relative z-10 grid md:grid-cols-2 gap-12 items-center max-w-6xl w-full">

        {/* Left Side - Flip Car Cards (stacked column) */}
        <div className="flex flex-col items-center gap-8">
          {carData.map((car, index) => (
            <CarCard
              key={index}
              image={car.image}
              title={car.title}
              description={car.description}
            />
          ))}
        </div>

        {/* Right Side - Text */}
        <div className="text-center md:text-left text-black">
          <h2 className="text-4xl md:text-5xl font-bold leading-snug drop-shadow-lg">
            Let Your Arrival Announce Your Personality
          </h2>
          <h3 className="mt-4 text-2xl font-extrabold text-blue-600 tracking-wide">
            HIRE A CAR FROM US TODAY!
          </h3>
          <div className="mt-8">
            <button
              onClick={() => navigate("/cars")}
              className="btn "
            >
              ðŸš— Book Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
