import React, { useEffect, useState } from "react";


const Home = () => {
  const [cars, setCars] = useState([]);



  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center fw-bold">ðŸš— Available Cars</h2>
      <div className="row">
        {cars.map((car) => (
          <div key={car._id} className="col-md-4 mb-4">
            <div className="card shadow-sm h-100">
              {/* If you have car images in your DB */}
              {car.image && (
                <img
                  src={car.image}
                  className="card-img-top"
                  alt={car.name}
                  style={{ height: "200px", objectFit: "cover" }}
                />
              )}

              <div className="card-body">
                <h5 className="card-title">{car.name}</h5>
                <p className="card-text">
                  <strong>Location:</strong> {car.location} <br />
                  <strong>Price:</strong> Kshs{car.price}
                </p>
                <button className="btn btn-primary w-100">View Details</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
