import React from "react";
import { useNavigate } from "react-router-dom";

const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="min-h-screen bg-[url('/images/car.jpeg')] bg-cover bg-center flex flex-col items-center justify-center px-6">

      {/* Dark overlay for readability (optional) */}
      <div className="bg-black/50 w-full h-full absolute top-0 left-0"></div>

      {/* Content Section */}
      <div className="relative grid md:grid-cols-2 gap-8 items-center max-w-6xl z-10">

        {/* Left Side - Gradient Glow */}
        <div className="relative flex justify-center">
          <div className="absolute -top-10 -left-10 w-80 h-80 rounded-full bg-gradient-to-r from-blue-500 to-purple-600 opacity-70 blur-2xl" />
        </div>

        {/* Right Side - Text */}
        <div className="text-center md:text-left text-white">
          <h2 className="text-3xl md:text-4xl font-semibold leading-snug">
            Let Your Arrival Announce Your Personality
          </h2>
          <h3 className="mt-4 text-2xl font-extrabold text-blue-300">
            HIRE A CAR FROM US TODAY!
          </h3>
          <div className="mt-6">
            <button
              onClick={() => navigate("/cars")}
              className="btn btn-primary"
            >
              Book Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
