import React from "react";
import { useNavigate } from "react-router-dom";
import "./src/App.css";


const Home = () => {
  const navigate = useNavigate();

  return (
    <div className="relative min-h-screen bg-[url('/images/car.jpeg')] bg-cover bg-center flex flex-col items-center justify-center px-6 text-white">
      {/* Overlay */}
      <div className="absolute inset-0 bg-black/60"></div>

      {/* Header Logo */}
      <div className="relative z-10 mb-12">
        <div className="logo-box text-3xl font-extrabold tracking-wider px-6 py-3 rounded-full border-4 border-white shadow-xl backdrop-blur-md">
          BMD
        </div>
      </div>

      {/* Content Section */}
      <div className="relative z-10 grid md:grid-cols-2 gap-12 items-center max-w-6xl w-full">
        {/* Left Side - Car Image */}
        <div className="relative flex justify-center">
          {/* Glow Effect */}
          <div className="absolute -top-12 -left-12 w-80 h-80 rounded-full bg-gradient-to-r from-blue-500 to-blue-300 opacity-30 blur-3xl animate-pulse" />

          {/* Circle Frame */}
          <div className="relative z-10 w-80 h-80 rounded-full overflow-hidden shadow-2xl border-8 border-blue-600 transform hover:scale-105 transition duration-500">
            <img
              src="/car.jpeg"
              alt="Luxury Car"
              className="w-full h-full object-cover"
            />
          </div>
        </div>

        {/* Right Side - Text */}
        <div className="text-center md:text-left">
          <h2 className="text-4xl md:text-5xl font-bold leading-snug drop-shadow-lg">
            Let Your Arrival Announce Your Personality
          </h2>
          <h3 className="mt-4 text-2xl font-extrabold text-blue-300 tracking-wide">
            HIRE A CAR FROM US TODAY!
          </h3>
          <div className="mt-8">
            <button
              onClick={() => navigate("/cars")}
              className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-purple-600 hover:to-blue-600 px-8 py-3 rounded-full text-lg font-semibold shadow-lg transform hover:scale-105 transition duration-300"
            >
              ðŸš— Book Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
