import React from "react";
import { useNavigate } from "react-router-dom";
import CarCard from "./CarCard"; // import the flip card component

const Home = () => {
  const navigate = useNavigate();

  // Example car data
  const carData = [
    {
      image: "/images/car1.jpg",
      title: "Mercedes S-Class",
      description: "A luxury sedan with unmatched comfort and performance."
    },
    {
      image: "/images/car2.jpg",
      title: "Range Rover Sport",
      description: "Powerful SUV for both city drives and off-road adventures."
    },
    {
      image: "/images/car3.jpg",
      title: "BMW M5",
      description: "High-performance sports sedan with a bold design."
    }
  ];
  return (
    <div className="min-h-screen bg-white flex flex-col items-center justify-center px-6">
      {/* Header Logo */}
      <div className="logo-container">
        <div className="logo-box">BMD</div>
      </div>

      {/* Content Section */}
      <div className="grid md:grid-cols-2 gap-8 items-center max-w-6xl">
        {/* Left Side - Car Image */}
        <div className="relative flex justify-center">
          <div className="absolute -top-10 -left-10 w-72 h-72 rounded-full bg-gradient-to-r from-blue-500 to-blue-300 opacity-40" />

          {/* Circle Frame for Image */}
          <div className="relative z-10 w-72 h-72 rounded-full overflow-hidden shadow-2xl border-8 border-blue-700">
            <img
              src="/car.jpeg"
              alt="Luxury Car"
              className="w-full h-full object-cover"
            />
          </div>
        </div>

        {/* Right Side - Text */}
        <div className="text-center md:text-left">
          <h2 className="text-3xl md:text-4xl font-semibold text-gray-800 leading-snug">
            Let Your Arrival Announce Your Personality
          </h2>
          <h3 className="mt-4 text-2xl font-extrabold text-blue-800">
            HIRE A CAR FROM US TODAY!
          </h3>
          <div className="mt-6">
            <button
              onClick={() => navigate("/cars")}
              className="btn btn-primary"
            >
              Book Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
