import React from "react";
import { useNavigate } from "react-router-dom";
import CarCard from "./CarCard"; // flip card component

const Home = () => {
  const navigate = useNavigate();

  // Example car data
  const carData = [
    {
      image: "/images/car1.jpg",
      title: "Mercedes S-Class",
      description: "A luxury sedan with unmatched comfort and performance."
    },
    {
      image: "/images/car2.jpg",
      title: "Range Rover Sport",
      description: "Powerful SUV for both city drives and off-road adventures."
    },
    {
      image: "/images/car3.jpg",
      title: "BMW M5",
      description: "High-performance sports sedan with a bold design."
    }
  ];

  return (
    <div className="relative min-h-screen bg-[url('/images/car.jpeg')] bg-cover bg-center flex flex-col items-center justify-center px-6">
      {/* Overlay */}
      <div className="absolute inset-0 bg-black/60"></div>

      {/* Content Section */}
      <div className="relative z-10 grid md:grid-cols-2 gap-12 items-center max-w-6xl w-full">

        {/* Left Side - Flip Car Cards */}
        <div className="flex flex-wrap justify-center gap-6">
          {carData.map((car, index) => (
            <CarCard
              key={index}
              image={car.image}
              title={car.title}
              description={car.description}
            />
          ))}
        </div>

        {/* Right Side - Text */}
        <div className="text-center md:text-left text-B">
          <h2 className="text-4xl md:text-5xl font-bold leading-snug drop-shadow-lg">
            Let Your Arrival Announce Your Personality
          </h2>
          <h3 className="mt-4 text-2xl font-extrabold text-blue-300 tracking-wide">
            HIRE A CAR FROM US TODAY!
          </h3>
          <div className="mt-8">
            <button
              onClick={() => navigate("/cars")}
              className="bg-gradient-to-r from-blue-600 to-purple-600 hover:from-purple-600 hover:to-blue-600 px-8 py-3 rounded-full text-lg font-semibold shadow-lg transform hover:scale-105 transition duration-300"
            >
              ðŸš— Book Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
